targets.query:
from(bucket: "heizung")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  //|> filter(fn: (r) => r._field =~ /^sp_(unten|mitte|oben)_temperature_C$/)
  //|> filter(fn: (r) => r._field =~ /^sp_{unten_mitte_oben:regex}_temperature_C$/)
  |> filter(fn: (r) => r._field =~ /^sp_(${unten_mitte_oben})_temperature_C$/)
  //|> filter(fn: (r) => r._field =~ /^${field_keys:regex}$/) // Versuch Peter
  |> filter(fn: (r) => r.etappe =~ /^${etappe:regex}$/)
  |> filter(fn: (r) => r.haus =~ /^${haus:regex}$/)
  |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)
  |> yield(name: "mean")

